#' Multilevel Reliability with Omega
#'
#' 
#' @param data .
#' @param items .
#' @param group .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$omega} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$omega$asDF}
#'
#' \code{as.data.frame(results$omega)}
#'
#' @export
omega <- function(
    data,
    items,
    group) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("omega requires jmvcore to be installed (restart may be required)")

    if ( ! missing(items)) items <- jmvcore::resolveQuo(jmvcore::enquo(items))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(items), items, NULL),
            `if`( ! missing(group), group, NULL))


    options <- omegaOptions$new(
        items = items,
        group = group)

    analysis <- omegaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

